    /**
     * check if itc macths with on e of th e integer on vector of Integer vi
     *
     * @param vi
     * @param itc
     * @return
     */
    public static boolean checkMatchOne(Vector vi, int itc) {
        if (vi != null) {
            int max = vi.size();
            try {
                for (int i = 0; i < max; i++) {
                    if (itc == ((Integer) vi.get(i)).intValue()) {
                        return true;
                    }
                }
            } catch (Exception exc) {
                System.out.println(" checkMatchOne " + exc);
                return false;
            }
        }
        return false;
    }

