package com.dimata.harisma.utility.service.presence;

import com.dimata.util.Formater;
import com.dimata.harisma.entity.service.*;
import java.util.*;

public class ServiceLateness implements Runnable {

    public ServiceLateness() {  
    }

    /**gadnyana
     * prosesnya ?
     * 1. Pencarian data employee
     * 2. pengecekan public holidays
     * 3. pencarian leave stock
     * 4. pencarian stock dp management
     * 5. proses pengubahan status dp dari NON_AKTIF menjadi AKTIF
     */
    public synchronized void run() 
    {        
        System.out.println(".................... ServiceLateness started ....................");
        LatenessAnalyser latenessAnalyser = new LatenessAnalyser();
        Date dt = latenessAnalyser.getDate();
        
        // added on 20040928
        boolean firstProcess = true;
        
        while (latenessAnalyser.running) 
        {
            Date dtTmp = new Date();
            Date dtNow = new Date(dtTmp.getYear(), dtTmp.getMonth(), dtTmp.getDate());            
            try 
            {             
                
                // added on 20040928
                if(firstProcess)
                {
                    // sleeping time for first process                    
                    ServiceConfiguration svcConf = PstServiceConfiguration.getSvcConfigurationByType(PstServiceConfiguration.SERVICE_TYPE_LATENESS);
                    int sleepTime = getSleepTime(new Date(), svcConf.getStartTime());                                       
                    System.out.println(".:: First process start running lateness services, thread now sleep/pause for "+(sleepTime / (60 * 1000))+" minutes ("+sleepTime+" ms)");
                    Thread.sleep(sleepTime);                                                               
                    
                    firstProcess = false;                                       
                
                }else{
                    
                    // memproses tanggal yang terlambat
                    System.out.println("dt    : " + dt);
                    System.out.println("dtNow : " + dtNow);                
                    if ( dt.getTime() < dtNow.getTime() )   
                    {                      
                        System.out.println("Process lateness on : " + dt);                    
                        latenessAnalyser.checkEmployeeLateness(dt,0,0,"","");
                        //update by satrya 2012-08-01
                        //latenessAnalyser.checkEmployeeLateness(dt,0,0,"");
                        dt.setDate(dt.getDate() + 1);     
                        Thread.sleep(500);   
                    }

                    // jika tanggal sekarang
                    else 
                    {                          
                        // sleeping time for next process
                        System.out.println("Do not process lateness on : " + dt);                    
                        ServiceConfiguration svcConf = PstServiceConfiguration.getSvcConfigurationByType(PstServiceConfiguration.SERVICE_TYPE_LATENESS);                        
                        
                        // convert periode (in minutes) to miliseconds (multiply by 60 * 1000)
                        int sleepTime = svcConf.getPeriode() * 60 * 1000;                        
                        System.out.println(".:: proses cek lateness finished, thread now sleep/pause for "+svcConf.getPeriode()+" minutes ("+sleepTime+" ms)");
                        Thread.sleep(sleepTime);                                            
                    }                                        
                }             
                
            }
            catch (Exception e) 
            {                
                System.out.println("Exc ServiceLateness : " + e.toString());  
            }
        }
    }
    
    
    /**
     * @param start
     * @param end
     * @return
     */    
    public int getSleepTime(Date start, Date end){
        Date s = new Date();
        Date e = new Date();
        
        s.setHours(start.getHours());
        s.setMinutes(start.getMinutes());
        s.setSeconds(start.getSeconds());
        
        e.setHours(end.getHours());
        e.setMinutes(end.getMinutes());
        e.setSeconds(end.getSeconds());
        
        if(end.getHours() < start.getHours())
        {
            int dtEnd = e.getDate();
            e.setDate(dtEnd+1);
        }        
        
        long st = s.getTime();
        long en = e.getTime();
        long rs = en - st;
        if(rs < 0)
        {
            rs = 0;
        }
        
        return (new Long(rs)).intValue();
    }    
    
}
